import React, { useState } from "react";
import Header from "./components/Header";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import About from "./pages/About";
import Home from "./pages/Home";
import Contact from "./pages/Contact";
import Datails from "./components/Datails";
import Login from "./pages/Login";

const App = () => {

  // const data = JSON.parse(localStorage.getItem('data'));
  let data = true;

  if(data) {
    return (
      <>
        <Router>
          <Header/>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/datails" element={<Datails />} />
          </Routes>
        </Router>
      </>
    );  
  } else {
    return(
      <Login/>
    )
  }
};

export default App;

// в неочивидной && операции с boolean, если первая часть true, то выводится вторая часть
// если первая часть false, то выводится первая часть
